import React, { Component } from "react";

export default class Player extends Component {
  componentDidMount() {
    const audioEl = document.getElementsByClassName("audio-element")[0];
    audioEl.play();
  }

  render() {
    return (
      <div>
        <audio className="audio-element">
          <source src={props.audio}></source>
        </audio>
      </div>
    );
  }
}
